import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}`);
        console.log('Fetched data:', response.data);

        let result = response.data;

        if (Array.isArray(result)) {
          setNews(result);
        } else if (result && Array.isArray(result.news)) {
          setNews(result.news);
        } else {
          throw new Error('API returned unexpected format');
        }

        setError(null);
      } catch (err) {
        console.error('Error fetching news:', err);
        setError('Failed to fetch news');
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, []);

  if (loading) return <div className="loading">Loading news...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="App">
      <header className="App-header">
        <h1>Latest News</h1>
      </header>
      <main className="news-container">
        {Array.isArray(news) &&
          news.map((item) => (
            <article key={item.id || item._id || item.title} className="news-card">
              <h2>{item.title}</h2>
              {item.company_name && (
                <p><strong>Company Name:</strong> {item.company_name}</p>
              )}
              {item.production_news && (
                <p><strong>Production News:</strong> {item.production_news}</p>
              )}
              {item.content && (
                <p><strong>Investment News:</strong> {item.content}</p>
              )}
              {item.investment_location && (
                <p><strong>Investment Location:</strong> {item.investment_location}</p>
              )}
              {item.url && typeof item.url === 'string' && (
                <p><strong>Website of company:</strong> <a href={item.url} target="_blank" rel="noopener noreferrer">{item.url}</a></p>
              )}
            </article>
          ))}
      </main>
    </div>
  );
}

export default App;
